#!/bin/bash

#Colours
greenColour="\e[0;32m\033[1m"
endColour="\033[0m\e[0m"
redColour="\e[0;31m\033[1m"
blueColour="\e[0;34m\033[1m"
yellowColour="\e[0;33m\033[1m"
purpleColour="\e[0;35m\033[1m"
turquoiseColour="\e[0;36m\033[1m"
grayColour="\e[0;37m\033[1m"

# QTILE
echo -e "${redColour}QTILE INSTALLATION${endColour}\n"
sudo pip3 install xcffib
sudo pip3 install --no-cache-dir cairocffi
sudo pip3 install qtile
mkdir -p $HOME/.config/qtile
echo "" | base64 -d > $HOME/.config/qtile/config.py

export PATH=$HOME/bin/.local:$PATH

echo "W0Rlc2t0b3AgRW50cnldCk5hbWU9UXRpbGUKQ29tbWVudD1RdGlsZSBTZXNzaW9uCkV4ZWM9cXRpbGUgc3RhcnQKVHlwZT1BcHBsaWNhdGlvbgpLZXl3b3Jkcz13bTt0aWxpbmcK" | base64 -d > qtile.desktop
sudo mv qtile.desktop /usr/share/xsessions

# APT
sudo apt install gnome-terminal feh rofi xclip neofetch -y

wget "https://wallpapercave.com/wp/wp1810645.jpg" -O $HOME/.config/qtile/wallpaper.png

echo -e "${purpleColour}==============================================================${endColour}"
echo -e "${redColour}FONTS CONFIGURATION${endColour}\n"
# HACK NERD FONTS
sudo wget "https://github.com/ryanoasis/nerd-fonts/releases/download/v2.3.3/Hack.zip" -O /usr/share/fonts/Hack.zip
sudo wget "https://github.com/ryanoasis/nerd-fonts/releases/download/v2.3.3/HeavyData.zip" -O /usr/share/fonts/HeavyData.zip
sudo 7z x /usr/share/fonts/Hack.zip
sudo 7z x /usr/share/fonts/HeavyData.zip
sudo rm -rf /usr/share/fonts/*.zip

echo -e "${purpleColour}==============================================================${endColour}"
echo -e "${redColour}FILES CONFIGURATION\n${endColour}"
echo "c2V0IHRhYnNpemUgNApzZXQgYXV0b2luZGVudApzZXQgbm93cmFwCnNldCBsaW5lbnVtYmVycwpzZXQgbm9oZWxwCnNldCBzb2Z0d3JhcAo=" | base64 -d > .nanorc
# LSD
wget https://github.com/Peltoche/lsd/releases/download/0.23.1/lsd_0.23.1_amd64.deb -O lsd_0.23.1_amd64.deb
sudo dpkg -i lsd_0.23.1_amd64.deb
# BAT
wget "https://github.com/sharkdp/bat/releases/download/v0.22.1/bat_0.22.1_amd64.deb" -O bat_0.22.1_amd64.deb
sudo dpkg -i bat_0.22.1_amd64.deb
rm -rf lsd_0.23.1_amd64.deb bat_0.22.1_amd64.deb

echo -e "${purpleColour}==============================================================${endColour}"
echo -e "${redColour}TMUX CONFIGURATION\n${endColour}"
git clone https://github.com/jimeh/tmux-themepack.git ~/.tmux-themepack
echo "source-file $HOME/.tmux-themepack/powerline/default/gray.tmuxtheme" > $HOME/.tmux.conf
echo "" | base64 -d > .tmux.conf

echo -e "${purpleColour}==============================================================${endColour}"
echo -e "${redColour}ZSH CONFIGURATION\n${endColour}"
git clone --depth=1 https://github.com/romkatv/powerlevel10k.git ~/.powerlevel10k
echo 'source ~/.powerlevel10k/powerlevel10k.zsh-theme' >>~/.zshrc
# ZSH SUDO
sudo mkdir /usr/share/zsh-sudo
sudo wget "https://raw.githubusercontent.com/ohmyzsh/ohmyzsh/master/plugins/sudo/sudo.plugin.zsh" -O /usr/share/zsh-sudo/sudo.plugin.zsh

echo -e "${endColour}==============================================================${endColour}"
echo -e "${endColour}SCRIPTS\n${endColour}"
echo "" | base64 -d > decode
sudo mv decode /usr/local/bin/decode
sudo chmod +x /usr/local/bin/decode

echo "" | base64 -d > encode
sudo mv encode /usr/local/bin/encode
sudo chmod +x /usr/local/bin/encode

echo "" | base64 -d > rtmux
sudo mv rtmux /usr/local/bin/rtmux
sudo chmod +x /usr/local/bin/rtmux


echo "" | base64 -d > scan
sudo mv scan /usr/local/bin/scan
sudo chmod +x /usr/local/bin/scan

# ROFI
sudo wget "https://raw.githubusercontent.com/newmanls/rofi-themes-collection/master/themes/nord.rasi" -O /usr/share/rofi/themes/nord.rasi

# FZF
git clone --depth 1 https://github.com/junegunn/fzf.git ~/.fzf
~/.fzf/install

# LIGHTDM
echo "" | base64 -d > gtk.css
sudo mv gtk.css /usr/share/themes/Kali-Light/gtk-3.0/gtk.css
sudo wget "https://iitd.com.ua/wp-content/uploads/2021/03/evolving-cybercriminals.jpg" -O /etc/lightdm/binary.png
sudo wget "https://www.pngkit.com/png/full/18-181084_anonymousemblem-logo-anonymous-png.png" -O /etc/lightdm/anonymous.png
sudo lightdm-gtk-greeter-settings
# heavy nerd font bold

# FOLDERS
mkdir -p $HOME/Downloads/vpn
mkdir -p $HOME/Files
mkdir -p $HOME/Files/CHEETSHEAT

# P10k
echo "" | base64 -d > .p10k.zsh

echo "" | base64 -d > .zshrc

# Choose Nerd Font

sudo chmod +x /usr/local/bin/*
